import { Component, OnInit, Input } from '@angular/core';

@Component({
  selector: 'app-button',
  templateUrl: './button.component.html',
  styleUrls: ['./button.component.css']
})
export class ButtonComponent implements OnInit{

  @Input() text : string;
  @Input() color : string;

  constructor(text : string, color : string) { 
    this.text = text
    this.color = color
  }

  ngOnInit(): void {

  }

}
