  selector: 'app-task-item',
  templateUrl: './task-item.component.html',
  styleUrls: ['./task-item.component.css']
})
export class TaskItemComponent implements OnInit{

  @Input() task: Task;

  constructor () { 
    this.task = TASKS[0]
  }

  ngOnInit(): void {

  }

}
